INSERT INTO `not_famous_places` (`id`, `place_name`, `img`, `location`, `information`) VALUES
(1, 'Буцький каньйон', 'butskyi_kanyon.jpg', 'смт Буки Маньківський район Черкаська область', 'Каньйон бере початок від греблі старої гідроелектростанції. Гребля та подалі вузьке, порожисте русло річки Гірський Тікич утворюють водоспад Вир. Каньйон являє собою оригінальний скелястий берег з виступами сірого граніту, заввишки близько 30 метрів. Довжина каньйону бл. 2,5 км, ширина — 80 м. Площа, що підлягає природоохоронним заходам — 80 га. Також каньйон пасує для скелелазіння та сплавів на байдарках.'),
(2, 'Тараканівський форт', 'tarakanivskiy_fort.jpeg', 'с Тараканів Дубенський район Рівненська область', 'Тараканівський форт - оборонна споруда, архітектурна пам\'ятка XIX століття. Форт має форму ромба зі сторонами близько 240 метрів. Ззовні він оточений глибоким ровом із земляними валами та укріпленими потужними стінами. В центральній частині форту зведена двоповерхова казарма, до якої ведуть чотири підземні ходи, прокладені під другим земляним валом. Там розташовувались житлові, складські та господарські приміщення для артилерійської роти та штаб коменданта форту. Периметр форту складений зі 105 так званих безпечних казематів і, щоб до них потрапити, треба було подолати подвійну лінію оборонних рубежів.\r\nЗа даними місцевих тут можна зустріти привидів.'),
(3, 'Музей в Переяслав-Хмельницький', 'museuam_Pereyaslav.jpg', 'м Переяслав-Хмельницький Київська область', 'Музей народної архітектури та побуту Середньої Наддніпрянщини — музей просто неба, розташований в околицях Переяслава. В музеї можна побачити яким було українське село Середньої Наддніпрянщини кінця ХІХ — початку ХХ століть, також будівлі та стоянки від часів пізнього палеоліту до часів Київської Русі. Музей ознайомлює з народною культурою, архітектурою та творчістю, звичаями та обрядами українців Середньої Наддніпрянщини. Варті уваги відвідувачів 122 пам\'ятки народної архітектури та понад 30 тисяч пам\'яток матеріальної і духовної культури.'),
(4, 'Українська Венеція', 'Vylkovo.jpg', 'м Вилкове Кілійський район Одеська область', 'Вилкове — місто, розташоване в українській частині дельти Дунаю. Це останній населений пункт на берегах Дунаю перед його впадінням в Чорне море. Це містечко називають українською Венецією тому, що переважна частина поселення розташована на воді. Тут є дороги, проте люди тут пересуваються на місцевих \"гондолах\" і моторних човнах.'),
(5, 'Заліщики', 'Zalishchyky.jpg', 'м Заліщики Тернопільська область', 'Заліщики – невеличке курортне містечко на лівому березі Дністра в південно-західному Поділлі, немов би на півострові. Також окрім красивого вигляду самого містечка, в ньому є багато туристичних принад: палац баронів Бруніцьких, Гінкго Ігнатія Бруніцького, костел Святого Станіслава. Головною прикрасою цього об\'єкта є панорама міста з меандром Дністра з протилежного, буковинського берегу'),
(6, 'Лемурійське озеро', 'Lemuryiske_ozero.jpg', 'Чаплинський район Херсонська область', 'Лемурійське озеро — озеро в Херсонській області України, недалеко від сіл Григорівка (Чаплинського району) та Нововолодимирівка, на східній частині (бухта) затоки Сиваш. Зачаровоє своїм рожевим кольором води. Солоність води озера сягає 270-300 грамів солі на літр води, тому це озеро також називають мертвим.'),
(7, 'Олешківські піски', 'Oleshkivska_pustelya.jpg', 'Олешківський район Херсонська область', 'Олешківські піски — це друга за розмірами пустеля в Європі. Розміри цього піщаного масиву вражають, це понад 200 тисяч гектарів'),
(8, 'Свірзький замок', 'svirzhskij_zamok.jpg', 'с Свірж Перемишлянський район Львівська область', 'Серед мальовничої природи Львівщини, на пагорбі височіють білокам\'яні вежі Свірзького замку. Фортечний комплекс розташований у селі Свірж, передмісті Львова. Щоб потрапити до споруди, треба перейти через міст над ровом.'),
(9, 'Гора Бона', 'gora_Bona.jpg', 'м Крименець Тернопільська область', 'Замковою горою або гора Бона –  гора-останець, що у Кременці, Тернопільської області. Це місто обросло легендами та історіями. Саме це і манить до неї туристів. Особлива атмосфера панує і в самому місті, адже його історія безпосередньо пов’язана з Замковою горою, на якій за легендами, жила жорстока королева.');

