INSERT INTO `seven_wonders` (`id`, `place_name`, `img`, `location`, `information`) VALUES
(1, 'Софіївський собор', 'Sophia_Kyiv.jpg', 'Місто Київ', 'Софія Київська - один з багатьох пам\'яток архітектури, який зберігся від часів Київської Русі.\r\n  Храм пережив зруйнування, спалювання, грабунки й безліч інших випробувань. Та все ж з гордістю встояв.\r\nЦе місце зберігає 260м<sup>2</sup> мозаїки та 3000м<sup>2</sup> фресок.'),
(2, 'Заповідник \"Кам\'янець\"', 'Kamianets-Podilskyi.jpg', 'Місто Кам\'янець-Подільський', 'Давнє місто, що підноситься стінами та вежами над скелястими берегами річки Смотрич. У недалекому минулому містечко було політичним, економічним і культурним центром на південному заході України. Неабиякий інтерес викликає і унікальний Замковий міст, що сполучає Старе місто із Замковим комплексом. До складу Кам’янецької фортеці входять одинадцять башт, кожна з яких має свою назву й історію.'),
(3, 'Дендропарк \"Софіївка\"', 'Sofiivka.jpeg', 'Місто Умань', 'Парк \"Софіївка\" був заснований графом Станіславом Потоцьким на честь своєї дружини Софії.\r\nМальовничий куточок, що вражає своєю неповторною красою. дивовижний витвір\r\nлюдської фантазії й майстерності, в якому переплітаються разом і світ загадкових легенд та міфів, і мистецтво, і краса природи.'),
(4, 'Києво-Печерська Лавра', 'Kyiv_Pecherska.jpg', 'Місто Київ', 'Монастир, який розкинувся на високих київських кручах над річкою Дніпро. Це один з перших на нашій землі осередок християнства, культури і науки.\r\nКраса не лише на поверхні, а й під землею, де розміщено унікальні комплекси, дальні та ближні печери.'),
(5, 'Хотинська Фортеця', 'Khotynska_fortetsya.jpg', 'Місто Хотин', 'Хотин – одне з найдавніших і найвідоміших міст України з великою історією.\r\nХотин був заснований на початку ХІ ст. київським князем Володимиром Великим, як одна із важливих складових прикордонної системи Русі. Фортеця у той період була дерев’яно-земляною і використовувалася давніми слов’янами для захисту своїх поселень. Хотин завжди привертав увагу завойовників. З метою захисту від них була споруджена фортеця, яка пережила століття і бачила під своїми мурами полчища воїнів Османської імперії, повстанців Мухи, народних месників Дитинки, вояків Дмитра Вишневецького (Байди), Петра Дорошенка.'),
(6, 'Хортиця', 'Khortytsya.png', 'Запорізька область', 'Острів Хортиця – найбільший острів на Дніпрі. Дивовижний краєвид та славна минувшина привертають увагу до острова мандрівників із різних куточків України\r\nта світу. Хортицю прославляли козаки, поети і художники, вивчали історики та краєзнавці.\r\nВікова історія Хортиці має героїчні та трагічні сторінки, овіяні\r\nлегендами далекого минулого, щедро оспівані народом.'),
(7, 'Херсонес', 'Khersones.jpg', 'Місто Севастополь', '<h4>(тимчасово окупована територія внаслідок збройної агресії Російської Федерації проти України, яка розпочалася 20 лютого 2014 року і триває донині)</h4>\r\n\r\n<p>Херсонес Таврійський – унікальна історико-археологічна пам’ятка\r\n        Вже два тисячоліття він визнаний осередком античної та візантійської культур на межі з безкраїм варварським світом. Місто було огороджене могутніми оборонними стінами і розплановане за схемою, поширеною тоді у грецькому та римському світах. До наших днів збереглося багато давніх споруд.</p>');

